mapper.update:
update sell
set is_sell = #{isSell,jdbcType=INTEGER},
  good_id = #{goodId,jdbcType=VARCHAR},
  user_id = #{userId,jdbcType=VARCHAR}
where id_sell = #{idSell,jdbcType=VARCHAR}

mapper.select:
select id_sell, is_sell, good_id, user_id
from sell
where id_sell = #{idSell,jdbcType=VARCHAR}
